package com.example.sondouni;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.view.View;
import android.widget.ImageView;

import com.bumptech.glide.Glide;

public class ImageViewActivity extends AppCompatActivity {
    private ImageView ivImg;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_image_view);
        ivImg = findViewById(R.id.ivImg);

        String imgUrl = "https://coinpan.com/files/attach/images/198/035/715/253/2a6620a621733d30211df8672c6b3557.jpg";
        Glide.with(this).load(imgUrl).into(ivImg);
        ivImg.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                String imgUrl = "data:image/jpeg;base64,";
                Glide.with(ivImg).load(imgUrl).into(ivImg);
            }
        });
    }
}